INSERT INTO products (id, api_id, product_id, name, price, rating, brand, link, description, status) VALUES (DEFAULT, 2, '1045', 'No Filter Foundation', 12.0, NULL, 'colourpop', 'https://colourpop.com/products/no-filter-matte-foundation', 'Developed for the Selfie Age, our buildable full coverage, natural matte foundation delivers flawless looking skin from day-to-night. The oil-free, lightweight formula blends smoothly and is easily customizable to create the coverage you want. Build it up or sheer it out, it was developed with innovative soft-blurring pigments to deliver true color while looking and feeling natural. The lockable pump is easy to use and keeps your routine mess-free! As always, 100% cruelty-free and vegan.', 0);
INSERT INTO products (id, api_id, product_id, name, price, rating, brand, link, description, status) VALUES (DEFAULT, 2, '1044', 'Lipstick', 26.0, NULL, 'boosh', 'https://www.boosh.ca/collections/all', 'All of our products are free from lead and heavy metals, parabens, phthalates, artificial colourants, and synthetic fragrances.  Boosh lipstick glides on smoothly for clean & protective SPF coverage. They are filled with hydrating oils and butters to preserve and enhance your lips natural surface. Organic sweet orange oil gives a light and cheerful scent.', 0);
INSERT INTO products (id, api_id, product_id, name, price, rating, brand, link, description, status) VALUES (DEFAULT, 2, '1043', 'Serum Foundation', 6.7, NULL, 'deciem', 'https://deciem.com/product/rdn-serum-foundation-30-r-30ml', 'Serum Foundations are lightweight medium-coverage formulations available in a comprehensive shade range across 21 shades. These foundations offer moderate coverage that looks natural with a very lightweight serum feel. They are very low in viscosity and are dispensed with the supplied pump or with the optional glass dropper available for purchase separately if preferred. ', 0);
INSERT INTO products (id, api_id, product_id, name, price, rating, brand, link, description, status) VALUES (DEFAULT, 2, '1042', 'Coverage Foundation', 6.9, NULL, 'deciem', 'https://deciem.com/product/rdn-coverage-foundation-33-n-30ml', 'Coverage Foundations are full-coverage formulations available in a comprehensive shade range across 21 shades. These foundations contain higher pigment levels than our Serum Foundations but still offer a smooth finish that avoids the heavy makeup look that can make skin appear more aged. The texture is that of a lightweight, non-oily cream.', 0);
INSERT INTO products (id, api_id, product_id, name, price, rating, brand, link, description, status) VALUES (DEFAULT, 2, '1041', 'Liquid Liner', 0.0, NULL, 'zorah biocosmetiques', 'https://www.purpicks.com/product/zorah-biocosmetiques-liquid-liner/', '<strong>12 hours of long-lasting</strong> intense color, transfer-free (leaves no trace on crease above the eyelid) <strong>Pure Light Capture®</strong> <strong>minerals</strong> deliver color and radiance. Silky lines and refreshingly light, Pure Argan eyeliner leaves a weightless feel on the eyelids.<p align=''LEFT''>Natural cosmetic certified by Ecocert Greenlife according to Ecocert Standard available at: http://cosmetiques.ecocert.com</p>98% of the total ingredients are from natural origin 5% of total ingredients are from organic farming', 0);
INSERT INTO products (id, api_id, product_id, name, price, rating, brand, link, description, status) VALUES (DEFAULT, 2, '1040', 'Eyeshadow', 0.0, NULL, 'zorah biocosmetiques', 'https://www.purpicks.com/product/zorah-eyeshadow/', '<strong>Product Description</strong>Anti-aging formula made with organic Pure Argan oil and Pure Light Capture<sup>®</sup> pigments. 24 Hour crease-free intensity (no build-up increase of eyelid), no running. Pure Light Capture® pigments deliver color and luminosity. Silky and refreshingly light feel, Pure Argan eyeshadow is weightless on the eyelids. Pure Argan eyeshadow <strong>soothes</strong>, <strong>hides redness </strong>and <strong>slows the appearance of the signs of aging on the skin.</strong><p align=''LEFT''>Natural cosmetic certified by Ecocert Greenlife according to Ecocert Standard available at: http://cosmetiques.ecocert.com</p>100% of the total ingredients are from natural origin5% of total ingredients are from organic farming', 0);
INSERT INTO products (id, api_id, product_id, name, price, rating, brand, link, description, status) VALUES (DEFAULT, 2, '1039', ' Realist Invisible Setting Powder', 0.0, NULL, 'w3llpeople', 'https://www.purpicks.com/product/w3llpeople-realist-invisible-setting-powder/', '<span style="font-weight: 400;">Keep it real with a polished, soft-focus look using this superfine, loose mineral powder. The healing properties of organic aloe help soothe and calm stressed out skin. Instantly minimize pores, soften imperfections, and creates a matte finish with superfine powder. Use by itself for an air-brushed look, or to set foundation for ultra-long wear.</span><span style="font-weight: 400;">* Set makeup or by itself for matte air-brushed effect</span><span style="font-weight: 400;">* Absorbs excess oil while softening fine lines</span><span style="font-weight: 400;">* Organic aloe creates radiant, rejuvenated skin</span><span style="font-weight: 400;">* Hypoallergenic, Chemical Free, Cruelty Free &amp; Noncomedogenic </span><span style="font-weight: 400;">* EWG Verified for non-toxic health and safety</span><span style="font-weight: 400;">* Free From: Parabens, Gluten, GMO, Phthalates, Sulfates, Fragrance, Pheonoxyethanol, Dimethicone, PEG, Propylene Glycol, Butylene Glycol, Lead, Mineral Oil, Talc</span>', 0);
INSERT INTO products (id, api_id, product_id, name, price, rating, brand, link, description, status) VALUES (DEFAULT, 2, '1038', 'B Smudged', 0.0, NULL, 'sally b''s skin yummies', 'https://www.purpicks.com/product/sally-bs-skin-yummies-b-smudged/', 'Let your eyes naturally pop with our B Smudged, a subtle eye color that adds a tint of color to the base of your lashes. An organic, cream eye color, B Smudged eliminates the inevitable uneven line from traditional eyeliners, and does not require expert blending techniques of messy, powder-based shadows. Simply ''smudge'' along lash line for color that stays in place for a long lasting look.', 0);
INSERT INTO products (id, api_id, product_id, name, price, rating, brand, link, description, status) VALUES (DEFAULT, 2, '1037', 'B Glossy Lip Gloss', 0.0, NULL, 'sally b''s skin yummies', 'https://www.purpicks.com/product/sally-bs-skin-yummies-b-glossy-lip-gloss/', 'Intensify your natural lip color with Sally B''s B Glossy Lip Gloss, an organic lip gloss that adds a silky smooth hint of color and shimmer to lips. Formulated with 98% certified organic, moisture-rich ingredients our B Glossy Lip Gloss works to enrich lip health for lips that feel good and look even better.', 0);
